   public void keyPressFX2D() {
    if (key == CODED) {
      switch(keyCode) {
        case ALT:
          this.holding_alt = true;
          break;
        case CONTROL:
          this.holding_ctrl = true;
          break;
        case SHIFT:
          this.holding_shift = true;
          break;
        case LEFT:
          this.holding_left = true;
          break;
        case RIGHT:
          this.holding_right = true;
          break;
        case UP:
          this.holding_up = true;
          break;
        case DOWN:
          this.holding_down = true;
          break;
      }
    }
    else {
      this.fixKeyFX2D();
      if (key == ' ') {
        this.holding_space = true;
      }
    }
  }
   public void keyReleaseFX2D() {
    if (key == CODED) {
      switch(keyCode) {
        case ALT:
          this.holding_alt = false;
          break;
        case CONTROL:
          this.holding_ctrl = false;
          break;
        case SHIFT:
          this.holding_shift = false;
          break;
        case LEFT:
          this.holding_left = false;
          break;
        case RIGHT:
          this.holding_right = false;
          break;
        case UP:
          this.holding_up = false;
          break;
        case DOWN:
          this.holding_down = false;
          break;
      }
    }
    else {
      this.fixKeyFX2D();
      if (key == ' ') {
        this.holding_space = false;
      }
    }
  }
   public void fixKeyFX2D() {
    if (!this.holding_shift) {
      if (key >= 'A' && key <= 'Z') {
        key += 32;
      }
      else {
        switch(key) {
          case 192:
            key = '`';
            break;
          case 222:
            key = '\'';
            break;
        }
      }
    }
    else {
      switch(key) {
        case '1':
          key = '!';
          break;
        case '2':
          key = '@';
          break;
        case '3':
          key = '#';
          break;
        case '4':
          key = '$';
          break;
        case '5':
          key = '%';
          break;
        case '6':
          key = '^';
          break;
        case '7':
          key = '&';
          break;
        case '8':
          key = '*';
          break;
        case '9':
          key = '(';
          break;
        case '0':
          key = ')';
          break;
        case '-':
          key = '_';
          break;
        case '=':
          key = '+';
          break;
        case '[':
          key = '{';
          break;
        case ']':
          key = '}';
          break;
        case '\\':
          key = '|';
          break;
        case ';':
          key = ':';
          break;
        case '\'':
          key = '\"';
          break;
        case ',':
          key = '<';
          break;
        case '.':
          key = '>';
          break;
        case '/':
          key = '?';
          break;
        case 192:
          key = '~';
          break;
        case 222:
          key = '\"';
          break;
      }
    }
  }

