   public void addStack() {
    this.addStack(1);
  }
   public void addStack(int amount) {
    this.stack += amount;
    if (this.stack <= 0) {
      this.remove = true;
    }
    if (this.stack > this.maxStack()) {
      global.errorMessage("ERROR: Stack of " + this.display_name() + " too big.");
    }
  }

   public void removeStack() {
    this.removeStack(1);
  }
   public void removeStack(int amount) {
    this.stack -= amount;
    if (this.stack <= 0) {
      this.remove = true;
    }
  }

