    if (this.curr_level != null) {
      this.curr_level.mousePress();
    }
    else if (this.curr_map != null) {
      this.curr_map.mousePress();
    }
    else if (this.curr_area != null) {
      this.curr_area.mousePress();
    }
    this.leftPanel.mousePress();
